using BlogSitesi.Models;
using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;

namespace BlogSitesi.DAL.EntityConfiguration
{
    public class MakaleCFG : IEntityTypeConfiguration<Makale>
    {
        public void Configure(EntityTypeBuilder<Makale> builder)
        {
            builder.HasOne(x => x.Kullanici).WithMany(x => x.Makaleler);
            builder.HasData(
          new Makale { MakaleID = 1, Baslik = "Felsefe Nedir?", Icerik = "Felsefe, varlık, bilgi, ahlak, zihin ve dil gibi temel konuları sorgulayan ve bu konulara dair sistematik düşünceler geliştiren disiplinler bütünüdür. İnsanların evreni ve kendilerini anlamaya yönelik meraklarından doğmuştur. Akıl yürütme, eleştirel düşünme ve mantık kullanarak sorular sorar ve yanıtlar arar. Felsefe, hem bireysel hem de toplumsal düzeyde yaşamı derinlemesine inceleme çabasıdır.", KullaniciId = 1, OrtalamaOkumaSuresi="3 dakika" },
          new Makale { MakaleID = 2, Baslik = "Felsefe Neden Önemlidir?", Icerik = "Felsefe, düşünme ve sorgulama becerilerini geliştirerek eleştirel düşünceyi teşvik eder. İnsanların dünyayı ve kendilerini daha iyi anlamalarına yardımcı olur, etik ve ahlaki sorulara yanıtlar arar, ve toplumsal sorunlara çözümler sunar. Bu nedenle, bireysel ve toplumsal gelişim için önemlidir.Felsefe, bilim, sanat ve politika gibi birçok alana temel oluşturur. Sorgulama ve mantık yeteneklerini geliştirir, insanın bilgiye, hakikate ve adalete ulaşma çabalarını destekler. Bu yüzden, eğitimde ve günlük yaşamda vazgeçilmezdir.", KullaniciId = 1, OrtalamaOkumaSuresi = "5 dakika" },
          new Makale { MakaleID = 3, Baslik = "Psikoloji Nedir?", Icerik = "Psikoloji, insan davranışlarını ve zihinsel süreçleri inceleyen bilim dalıdır. Bireylerin düşünce, duygu ve davranışlarını anlamaya ve açıklamaya çalışır. Biyolojik, sosyal, gelişimsel ve klinik perspektiflerden insan deneyimini değerlendirir. Psikoloji, terapi ve danışmanlık yoluyla ruh sağlığını iyileştirir, öğrenme süreçlerini ve insan ilişkilerini geliştirir.", KullaniciId = 1, OrtalamaOkumaSuresi = "4 dakika" },
          new Makale { MakaleID = 4, Baslik = "Egzersizin Stres ve Anksiyete Üzerindeki Etkileri", Icerik = "Modern yaşamın getirdiği stres ve anksiyete, bireylerin ruh sağlığı üzerinde ciddi etkiler yaratmaktadır. Bu çalışma, düzenli egzersizin stres ve anksiyete seviyeleri üzerindeki etkilerini incelemeyi amaçlamaktadır.Araştırma, 18-45 yaş arası 150 katılımcı ile yürütülmüştür. Katılımcılar, düzenli egzersiz yapanlar ve yapmayanlar olarak iki gruba ayrılmıştır. Egzersiz yapan grup haftada en az üç kez 30 dakika süreyle egzersiz yapmaktadır. Stres seviyeleri Perceived Stress Scale (PSS) ve anksiyete seviyeleri State-Trait Anxiety Inventory (STAI) ile ölçülmüştür.Araştırma sonuçlarına göre, düzenli egzersiz yapan katılımcıların stres ve anksiyete seviyeleri, egzersiz yapmayanlara göre anlamlı derecede düşük bulunmuştur. Egzersiz süresi arttıkça, stres ve anksiyete seviyelerinin daha da azaldığı gözlemlenmiştir.Araştırma, düzenli egzersizin stres ve anksiyete seviyelerini düşürmede önemli bir rol oynadığını ortaya koymuştur. Bu bulgular, ruh sağlığını koruma ve iyileştirme çabalarında egzersizin teşvik edilmesi gerektiğini göstermektedir. Sağlık profesyonelleri ve danışmanlar, bireylere düzenli egzersiz yapmalarını önererek, onların stres ve anksiyeteyle daha etkin bir şekilde başa çıkmalarına yardımcı olabilir.", KullaniciId = 1, OrtalamaOkumaSuresi = "10 dakika" },
          new Makale { MakaleID = 5, Baslik = "Eğlencenin Bireysel ve Toplumsal Refah Üzerindeki Etkileri", Icerik = "Eğlence, bireylerin günlük yaşam stresinden uzaklaşmalarını sağlayan ve genel refahı artıran önemli bir faaliyettir. Bu tez, eğlencenin bireysel mutluluk ve toplumsal bağlar üzerindeki etkilerini incelemeyi amaçlamaktadır.Anket ve gözlem yöntemleri kullanılarak 300 katılımcının eğlence alışkanlıkları, yaşam memnuniyeti ve sosyal ilişkileri değerlendirilmiştir.Araştırma sonuçlarına göre, düzenli eğlence etkinliklerine katılan bireyler, daha yüksek yaşam memnuniyeti ve daha güçlü sosyal bağlar bildirmiştir. Eğlence, sosyal etkileşimi artırarak toplumsal bütünleşmeye katkıda bulunmaktadır.Eğlence, bireysel ve toplumsal refahı artıran önemli bir unsurdur. Bu nedenle, bireylerin eğlenceye daha fazla zaman ayırmaları teşvik edilmelidir.", KullaniciId = 1 , OrtalamaOkumaSuresi = "5 dakika" },
          new Makale { MakaleID = 6, Baslik = "Eğitimde Teknolojinin Rolü ve Geleceği", Icerik = "Teknolojinin hızla gelişmesi, eğitim alanında da önemli değişimlere yol açmıştır. Bu makale, teknolojinin eğitimdeki rolünü ve gelecekteki etkilerini ele almaktadır.Makale, dijital öğrenme araçlarının kullanımı, uzaktan eğitim trendleri, yapay zeka destekli öğrenme ve sanal gerçeklik gibi teknolojik yenilikler üzerinde durmaktadır.Teknolojinin eğitimde kullanımı, öğrencilerin öğrenme deneyimini zenginleştirirken öğretmenlerin de öğretim yöntemlerini geliştirmelerine olanak tanımaktadır. Uzaktan eğitim, özellikle pandemi döneminde önemli bir araç haline gelmiştir.Makale, yapay zeka, artırılmış gerçeklik ve kişiselleştirilmiş öğrenme gibi teknolojik gelişmelerin eğitimde daha da belirleyici olacağını vurgulamaktadır.Eğitimde teknolojinin etkin ve bilinçli bir şekilde kullanılması, öğrenme süreçlerini iyileştirirken gelecek nesillerin yeteneklerini şekillendirmede önemli bir role sahiptir. Bu nedenle, teknolojiye dayalı eğitim stratejileri geliştirmek ve uygulamak gereklidir.", KullaniciId = 1 , OrtalamaOkumaSuresi = "10 dakika" }
          );
        }
    }
}
